mapper.select:
select * from (select rownum rn, a.* from (select n.*, d.dept_name from notice n
where post_category = #{category}
join admin a on n.writer_id = a.admin_id join lib_dept d on a.dept_id = d.dept_id order by fix desc, notice_id desc) a) where rn between #{start} and #{end}

mapper.update:
update notice
set view_count = view_count+1
where notice_id = #{notice_id}

mapper.select:
	select count(*)
    from notice

	where post_category = #{category}
